import './Card.css'
import Button from '../Button/Button';

function Card(props) {
    return (
        <div className='Card'>
            <img className='Card-img' src='https://s1.1zoom.ru/big7/984/Canada_Parks_Lake_Mountains_Forests_Scenery_Rocky_567540_2560x1600.jpg' alt="Canada park"></img>
            <div className='Card-container'>
                <h2 className='Card-title common-header'>{ props.title }</h2>
                <p className='Card-description'>{ props.description }</p>
                <div className='Card-price common-price'>{ props.price }</div>
                <Button text="Купить" />
            </div>
        </div>
    )
}

export default Card;
